import 'babel-polyfill';
import ReactDOM from 'react-dom';
import React from 'react';
import { combineReducers, compose, applyMiddleware, createStore } from 'redux';
import thunkMiddleware from 'redux-thunk';
import { Provider } from 'react-redux';
import { searchTermsReducer, feedReducer, searchOnlyDB } from './search/searchReducer';
import mostFrequentReducer from './results/mostfrequent/mostFrequentReducer';
import footballCategoryFiltersReducer from './search/categories/categoryFiltersReducer';
import { fetchAllFootballSeasons } from './search/categories/categoryFilterActions';
import moment from 'moment';

const feedApp = combineReducers({
  searchTerms: searchTermsReducer,
  mostFrequent: mostFrequentReducer,
  feed: feedReducer,
  football: footballCategoryFiltersReducer,
  searchOnlyDB,
});

const middlewares = [thunkMiddleware];

const finalStore = createStore(
  feedApp,
  compose(
    applyMiddleware(...middlewares),
    window.devToolsExtension ? window.devToolsExtension() : f => f
  )
);

// fetch all the football seasons on startup
finalStore.dispatch(fetchAllFootballSeasons(moment().year() - 1, true));

const rootElement = document.getElementById('root');

let render = () => {
  const App = require('./queryInterface').default;
  ReactDOM.render(
    <Provider store={finalStore}>
      <App />
    </Provider>,
    rootElement
  );
};

if (module.hot) {
  // Support hot reloading of components
  const renderApp = render;

  // and display an overlay for runtime errors
  const renderError = (error) => {
    const RedBox = require('redbox-react');
    ReactDOM.render(
      <RedBox error={error} />,
      rootElement
    );
  };

  render = () => {
    try {
      renderApp();
    } catch (error) {
      renderError(error);
    }
  };

  module.hot.accept('./queryInterface', () => {
    setTimeout(render);
  });
}

render();

import React, { Component } from 'react';
import Header from './Header';
import Search from './search/Search';
import Results from './results/Results.js';

/**
 * App is the core React component that describes our application interface.
 * Note how it is incredibly simple; it contains references to a {@link Header},
 * {@link Search}, {@link Results} and {@link Footer} component.
 * To see what is in the Search component (which is the most important),
 * follow the import reference seen above on line 3, and open the file
 * './search/Search.js'.
 */
const App = () => (
  <div>

    <Header />

    <Search />

    <Results />

    {/* <Footer /> *//* <Footer /> */}
  </div>
);

export default App;

import React, { Component } from 'react';
import { connect } from 'react-redux';
import moment from 'moment';
import { setFeedPageNumber, setFeedPageLimit } from '../search/searchActions';
import { TwitterProfilePicture } from '../Twitter';
import { fetchPost, newPromiseChain } from '../../shared/utilities';

class Feed extends Component {
  componentDidMount() {
    $('.popup').popup();
  }

  componentDidUpdate() {
    $('.popup').popup();
  }

  render() {
    const {
      feed, paginationInfo,
      toggledWords, isWordsToggledActionHide,
      toggledUsers, isUsersToggledActionHide,
    } = this.props;

    const filteredFeed = filterPostsForFeed(
      feed, toggledWords, isWordsToggledActionHide,
      toggledUsers, isUsersToggledActionHide
    );

    const paginatedFeed = paginatePosts(filteredFeed, paginationInfo);

    return (
      <div>
        <div className="ui two column grid">
          <div className="column">
            <h3>Search results</h3>
          </div>
          <div className="right aligned column">
            Showing {filteredFeed.length}/{feed.length} posts
          </div>
        </div>
        <div className="ui divided items">
          {paginatedFeed.map((feedItem) => (
            <FeedItem content={feedItem} key={feedItem.data.id} />
          ))}
        </div>
        <div>
          <PaginationButtons
            numberOfPages={Math.ceil(filteredFeed.length / paginationInfo.limit)}
            paginationInfo={paginationInfo}
          />
        </div>
      </div>
    );
  }
}
Feed.propTypes = {
  feed: React.PropTypes.array,
  paginationInfo: React.PropTypes.object,
  toggledWords: React.PropTypes.array,
  isWordsToggledActionHide: React.PropTypes.bool,
  toggledUsers: React.PropTypes.array,
  isUsersToggledActionHide: React.PropTypes.bool,
};

const filterPostsForFeed = (
  feed, toggledWords, isWordsToggledActionHide, toggledUsers, isUsersToggledActionHide
) => (
  feed.filter((feedItem) => {
    const content = feedItem.data.content;
    const authorId = feedItem.author.id;

    // If we can find the chosen hidden word in this tweet, block the post
    for (const toggledWord of toggledWords) {
      if (content.indexOf(toggledWord) > -1) {
        return !isWordsToggledActionHide && isUsersToggledActionHide;
      }
    }

    for (const toggledUser of toggledUsers) {
      if (authorId === toggledUser) {
        return !isUsersToggledActionHide && isWordsToggledActionHide;
      }
    }

    return isWordsToggledActionHide && isUsersToggledActionHide;
  }
));

const paginatePosts = (feed, paginationInfo) => {
  const pageNumber = paginationInfo.number;
  const pageLimit = paginationInfo.limit;

  const first = (pageNumber - 1) * pageLimit;
  const last = first + pageLimit;

  return feed.slice(first, last);
};

let PaginationButtons = ({ dispatch, numberOfPages, paginationInfo }) => (
  <div className="ui grid">
    <div className="two column row">
      <div className="left column">
        <LimitButtons
          paginationInfo={paginationInfo}
          updateLimit={(limit) => { dispatch(setFeedPageLimit(limit)); }} /> results per page.
      </div>
      <br />
      <div className="right aligned column">
        <PagePicker
          numberOfPages={numberOfPages}
          paginationInfo={paginationInfo}
          updatePageNumber={(limit) => { dispatch(setFeedPageNumber(limit)); }}
        />
      </div>
    </div>
  </div>
);
PaginationButtons = connect()(PaginationButtons);

const LimitButtons = ({ updateLimit, paginationInfo }) => (
  <div className="ui buttons">
    <LimitButton limit={10} paginationInfo={paginationInfo} updateLimit={updateLimit} />
    <LimitButton limit={25} paginationInfo={paginationInfo} updateLimit={updateLimit} />
    <LimitButton limit={50} paginationInfo={paginationInfo} updateLimit={updateLimit} />
  </div>
);

const PagePicker = ({ updatePageNumber, numberOfPages, paginationInfo }) => (
  <div className="ui right labeled input">
    <div className="ui label">Page</div>
    <input type="number"
      placeholder="Page number..."
      onChange={(e) => {
        const value = Math.min(e.target.value, numberOfPages);
        if (value !== '' && !isNaN(parseFloat(value)) && isFinite(value)) {
          e.target.value = value;
          updatePageNumber(value);
        }
      }}
      defaultValue={paginationInfo.number}
    />
    <div className="ui label">
      / {numberOfPages}
    </div>
  </div>
);

const LimitButton = ({ updateLimit, limit, paginationInfo }) => {
  const active = limit === paginationInfo.limit;
  return (
    <button
      className={`ui ${active ? 'blue' : ''} button`}
      onClick={() => {
        updateLimit(limit);
      }}>
      {limit}
    </button>
  );
};

const FeedItem = ({ content }) => {
  let post;
  switch (content.source) {
    case 'twitter': post = <Tweet content={content} />;
  }

  return (
    <div className="item">
      <div className="meta"
           style={{ minWidth: '40px', textAlign: 'center', verticalAlign: 'middle' }}
      >
        <i className={`${content.source} icon`} />
        <br />
      </div>
      {post}
    </div>
  );
};

const Tweet = ({ content }) => {
  // Inject HTML <a> tags around any Twitter approved t.co link
  const tweetWithLinks = content.data.content.replace(/(https\:\/\/t\.co\/.+?)\b/g, '<a href="$1">$1</a>');
  const decodedAuthorName = content.author.name;

  let goldStar;
  if (content.data.likes + content.data.retweets > 10) {
    goldStar = (<i className="yellow star icon popup" data-title="Popular tweet"/>);
  }

  let quotedContent;
  if (content.data.contains_a_quoted_tweet) {
    quotedContent = <QuotedTweet parentId={content.data.id}/>;
  }

  // Just below we use dangerousSetInnerHTML.
  // The content it is display has come from Twitter and is safe to render as actual HTML,
  // as all HTML entities have already been encoded - e.g., instead of <script> a tweet
  // would contain &lt;script&gt;, which is totally safe to render.

  return (
    <div className="content">
      <TwitterProfilePicture author={content.author} size="tiny" />

      {goldStar}
      <a href={`//twitter.com/${content.author.handle}`} target="_blank">
        <strong className="tweet fullname header">{decodedAuthorName}</strong>
        &nbsp;
        <span style={{color: '#A333C8'}}>@{content.author.handle}</span>
      </a>
      <br />
      <div dangerouslySetInnerHTML={{ __html: tweetWithLinks }} />

      {quotedContent}

      <div className="meta">
        <span className="date">
          <a href={`//twitter.com/${content.author.handle}/status/${content.data.id}`}>
            {moment(content.data.date).calendar()}
          </a>
        </span>
        |
        <span className="likes popup" data-title="Likes">
          <i className="like icon" />{content.data.likes}
        </span>
        |
        <span className="retweets popup" data-title="Retweets">
          <i className="retweet icon" />{content.data.retweets}
        </span>
      </div>
    </div>
  );
};

class QuotedTweet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tweetContent: null,
    };
  }

  componentDidMount() {
    fetch('/tweet/quotedby/' + this.props.parentId, {})
      .then((result) => result.json())
      .then(
        (result) => {
          this.setState({
            tweetContent: {
              data: result.tweet,
              author: result.author,
            },
          });
        },
        (rej) => {
          console.warn('Unable to fetch quoted tweet.', rej);
        }
      );
  }

  render() {
    let loadingClass = 'active';
    let displayableTweetContent;

    if (this.state.tweetContent !== null) {
      displayableTweetContent = (
        <div>
          <Tweet content={this.state.tweetContent} />
        </div>
      );
      loadingClass = '';
    };

    return (
      <div>
      <div className="ui icon message">
        <i className="left quote icon popup" data-title="Quoting tweet"/>
        <div className="content">
          <div className={`ui ${loadingClass} inverted dimmer`}>
            <div className="ui small text loader">Fetching quoted tweet</div>
          </div>
          {displayableTweetContent}
        </div>
      </div>
    </div>
    );
  }
};

export default Feed;

import React, { Component } from 'react';

export default class GoogleMap extends Component {
  // Inspired by http://revelry.co/google-maps-react/

  render() { return (<div />); };

  componentDidUpdate() {
    this.markerBounds = new google.maps.LatLngBounds();
    this.markerBounds.extend(new google.maps.LatLng(49.9, -9.8)); // South West of UK
    this.markerBounds.extend(new google.maps.LatLng(58.9, -1.93)); // North East of UK

    this.markers.forEach((marker) => marker.setMap(null));
    this.props.posts.forEach((post) => this.addMarker.bind(this)(post));

    // Fit the zoom of the map to show all of the marked tweets, and no more.
    this.map.fitBounds(this.markerBounds);
  }

  componentDidMount() {
    this.markers = [];
    this.map = this.createMap();

    this.componentDidUpdate();
  }

  createMap() {
    const mapOptions = {
      minZoom: 1,
      zoom: 1,
      center: new google.maps.LatLng(0, 0),
      mapTypeId: google.maps.MapTypeId.TERRAIN,
    };

    return new google.maps.Map($('#tweetMap')[0], mapOptions);
  }

  addMarker(post) {
    const pos = new google.maps.LatLng(post.data.latitude, post.data.longitude);

    const marker = new google.maps.Marker({
      position: pos,
      map: this.map,
      title: post.data.content,
    });

    const infowindow = new google.maps.InfoWindow({
      content: `<a href="//twitter.com/${post.author.handle}">@${post.author.handle}</a>: ${post.data.content}`,
    });

    marker.addListener('click', function () {
      infowindow.open(this.map, marker);
    });

    this.markers.push(marker);
    this.markerBounds.extend(pos);

    return marker;
  }
};

import React, { Component } from 'react';
import SlidingSearchBar from './SearchBar';

const MostFrequent = ({ title, filterTerm, onTypingInSearchBar, onToggleAll, currentToggledAction, children }) => (
  <div>
    <h3>{title}</h3>
    <div>
      <div className="ui two column grid">
        <div className="column">
          <SlidingSearchBar onTypingInSearchBar={onTypingInSearchBar}
            currentValue={filterTerm}
          />
        </div>
        <div className="right aligned column">
          <button className="ui purple basic button" onClick={onToggleAll}>
            {currentToggledAction ? 'Hide all' : 'Show all'}
          </button>
        </div>
      </div>
      <br />
      {children}
    </div>
  </div>
);

export default MostFrequent;

export const UPDATE_MOST_USED_WORDS_SEARCH_FILTER = 'UPDATE_MOST_USED_WORDS_SEARCH_FILTER';
export const updateMostUsedWordsSearch = (filterTerm) => ({
  type: UPDATE_MOST_USED_WORDS_SEARCH_FILTER,
  filterTerm,
});

export const UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER = 'UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER';
export const updateActiveUsersSearch = (filterTerm) => ({
  type: UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER,
  filterTerm,
});

export const TOGGLE_MOST_USED_WORDS = 'TOGGLE_MOST_USED_WORDS';
export const toggleMostUsedWords = (words) => ({
  type: TOGGLE_MOST_USED_WORDS,
  words,
});

export const TOGGLE_MOST_ACTIVE_USER = 'TOGGLE_MOST_ACTIVE_USER';
export const toggleMostActiveUser = (userId) => ({
  type: TOGGLE_MOST_ACTIVE_USER,
  userId,
});

export const TOGGLE_ALL_MOST_USED_WORDS = 'TOGGLE_ALL_MOST_USED_WORDS';
export const toggleAllMostUsedWordsSearch = () => ({
  type: TOGGLE_ALL_MOST_USED_WORDS,
});

export const TOGGLE_ALL_MOST_ACTIVE_USERS = 'TOGGLE_ALL_MOST_ACTIVE_USERS';
export const toggleAllMostActiveUsersSearch = () => ({
  type: TOGGLE_ALL_MOST_ACTIVE_USERS,
});

export const RESET_MOST_FREQUENT = 'RESET_MOST_FREQUENT';
export const resetMostFrequent = () => ({
  type: RESET_MOST_FREQUENT,
});

import { should } from 'chai';
import {
  UPDATE_MOST_USED_WORDS_SEARCH_FILTER, updateMostUsedWordsSearch,
  UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER, updateActiveUsersSearch,
  TOGGLE_MOST_USED_WORDS, toggleMostUsedWords,
  TOGGLE_MOST_ACTIVE_USER, toggleMostActiveUser,
  TOGGLE_ALL_MOST_USED_WORDS, toggleAllMostUsedWordsSearch,
  TOGGLE_ALL_MOST_ACTIVE_USERS, toggleAllMostActiveUsersSearch,
} from './mostFrequentActions';

describe('#MostFrequentActions', () => {
  it('should create a correct search for most  used words', () => {
    updateMostUsedWordsSearch('filter').should.deep.equal({
      type: UPDATE_MOST_USED_WORDS_SEARCH_FILTER,
      filterTerm: 'filter',
    });
  });

  it('should create a correct search for most active user', () => {
    updateActiveUsersSearch('filter').should.deep.equal({
      type: UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER,
      filterTerm: 'filter',
    });
  });

  it('should create an action for toggling hiding most used word', () => {
    toggleMostUsedWords(['exampleWord']).should.deep.equal({
      type: TOGGLE_MOST_USED_WORDS,
      words: ['exampleWord'],
    });
  });

  it('should create an action for toggling hiding most active user', () => {
    toggleMostActiveUser('12345').should.deep.equal({
      type: TOGGLE_MOST_ACTIVE_USER,
      userId: '12345',
    });
  });

  it('should create an action for toggling all most used words', () => {
    toggleAllMostUsedWordsSearch().should.deep.equal({
      type: TOGGLE_ALL_MOST_USED_WORDS,
    });
  });

  it('should create an action for toggling all most active users', () => {
    toggleAllMostActiveUsersSearch().should.deep.equal({
      type: TOGGLE_ALL_MOST_ACTIVE_USERS,
    });
  });
});

import { toggleArrayElement } from './../../../shared/utilities';
import {
  UPDATE_MOST_USED_WORDS_SEARCH_FILTER, TOGGLE_MOST_USED_WORDS,
  UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER, TOGGLE_MOST_ACTIVE_USER,
  TOGGLE_ALL_MOST_USED_WORDS, TOGGLE_ALL_MOST_ACTIVE_USERS,
  RESET_MOST_FREQUENT
} from './mostFrequentActions';

const mostFrequentInitialState = {
  filterTerm: '',
  toToggle: [],
  isToggledActionHide: true
};

const initialState = {
  words: mostFrequentInitialState,
  users: mostFrequentInitialState,
};

const mostFrequentReducer = (state = initialState, action) => {
  switch (action.type) {
  case RESET_MOST_FREQUENT:
    return initialState;
  case UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER:
  case TOGGLE_MOST_ACTIVE_USER:
  case TOGGLE_ALL_MOST_ACTIVE_USERS:
    return {
      ...state,
      users: users(state.users, action)
    };
  case UPDATE_MOST_USED_WORDS_SEARCH_FILTER:
  case TOGGLE_MOST_USED_WORDS:
  case TOGGLE_ALL_MOST_USED_WORDS:
    return {
      ...state,
      words: words(state.words, action)
    };
  default:
    return state;
  }
};

const words = (state, action) => {
  switch (action.type) {
  case UPDATE_MOST_USED_WORDS_SEARCH_FILTER:
    return {
      ...state,
      filterTerm: action.filterTerm,
    };
  case TOGGLE_MOST_USED_WORDS:
    return {
      ...state,
      toToggle: action.words.reduce(
        (toggledWords, word) => toggleArrayElement(toggledWords, word), state.toToggle
      ),
    };
  case TOGGLE_ALL_MOST_USED_WORDS:
    return {
      ...state,
      toToggle: [],
      isToggledActionHide: !state.isToggledActionHide,
    };
  default:
    return state;
  }
};

const users = (state, action) => {
  switch (action.type) {
  case UPDATE_MOST_ACTIVE_USERS_SEARCH_FILTER:
    return {
      ...state,
      filterTerm: action.filterTerm,
    };
  case TOGGLE_MOST_ACTIVE_USER:
    return {
      ...state,
      toToggle: toggleArrayElement(state.toToggle, action.userId),
    };
  case TOGGLE_ALL_MOST_ACTIVE_USERS:
    return {
      ...state,
      toToggle: [],
      isToggledActionHide: !state.isToggledActionHide,
    };
  default:
    return state;
  }
};

export default mostFrequentReducer;

import { should } from 'chai';
import deepFreeze from 'deep-freeze';
import mostFrequentReducer from './mostFrequentReducer';
import {
  updateMostUsedWordsSearch, toggleMostUsedWords, toggleAllMostUsedWordsSearch,
  updateActiveUsersSearch, toggleMostActiveUser, toggleAllMostActiveUsersSearch
} from './mostFrequentActions';

const initialState = {
  words: {
    filterTerm: '',
    toToggle: [],
    isToggledActionHide: true,
  },
  users: {
    filterTerm: '',
    toToggle: [],
    isToggledActionHide: true,
  },
};

describe('#MostFrequentReducer', () => {
  describe('Most used words', () => {
    it('should add the given search term to words search filter', () => {
      const stateBefore = initialState;
      const action = updateMostUsedWordsSearch('Football');

      const stateAfter = {
        words: {
          filterTerm: 'Football',
          toToggle: [],
          isToggledActionHide: true,
        },
        users: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: true,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should change toggle isToggledActionHide and empty the toToggle words', () => {
      const stateBefore = {
        words: {
          filterTerm: '',
          toToggle: ['lEmOn'],
          isToggledActionHide: true,
        },
        users: {
          filterTerm: '',
          toToggle: ['12345'],
          isToggledActionHide: true,
        },
      };
      const action = toggleAllMostUsedWordsSearch();

      const stateAfter = {
        words: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: false,
        },
        users: {
          filterTerm: '',
          toToggle: ['12345'],
          isToggledActionHide: true,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    describe('Toggling of hidden words', () => {
      it('should add a hidden word', () => {
        const stateBefore = initialState;
        const action = toggleMostUsedWords(['LeMoN']);

        const stateAfter = {
          words: {
            filterTerm: '',
            toToggle: ['LeMoN'],
            isToggledActionHide: true,
          },
          users: {
            filterTerm: '',
            toToggle: [],
            isToggledActionHide: true,
          },
        };

        deepFreeze(stateBefore);
        deepFreeze(action);

        mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
      });

      it('should not conflate similar hidden words', () => {
        const stateBefore = {
          words: {
            filterTerm: '',
            toToggle: ['lEmOn'],
            isToggledActionHide: true,
          },
          users: {
            filterTerm: '',
            toToggle: [],
            isToggledActionHide: true,
          },
        };
        const action = toggleMostUsedWords(['LeMoN']);

        const stateAfter = {
          words: {
            filterTerm: '',
            toToggle: ['lEmOn', 'LeMoN'],
            isToggledActionHide: true,
          },
          users: {
            filterTerm: '',
            toToggle: [],
            isToggledActionHide: true,
          },
        };

        deepFreeze(stateBefore);
        deepFreeze(action);

        mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
      });

      it('should show/remove a hidden word', () => {
        const stateBefore = {
          words: {
            filterTerm: '',
            toToggle: ['LEMON'],
            isToggledActionHide: true,
          },
          users: {
            filterTerm: '',
            toToggle: [],
            isToggledActionHide: true,
          },
        };
        const action = toggleMostUsedWords(['LEMON']);

        const stateAfter = initialState;

        deepFreeze(stateBefore);
        deepFreeze(action);

        mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
      });
    });
  });

  describe('Most active users', () => {
    it('should add the given search term for most active users', () => {
      const stateBefore = initialState;
      const action = updateActiveUsersSearch('Football');

      const stateAfter = {
        words: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: true,
        },
        users: {
          filterTerm: 'Football',
          toToggle: [],
          isToggledActionHide: true,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should change isToggledActionHide and empty the toToggle users', () => {
      const stateBefore = {
        words: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: true,
        },
        users: {
          filterTerm: '',
          toToggle: ['12345'],
          isToggledActionHide: true,
        },
      };
      const action = toggleAllMostActiveUsersSearch();

      const stateAfter = {
        words: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: true,
        },
        users: {
          filterTerm: '',
          toToggle: [],
          isToggledActionHide: false,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      mostFrequentReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    describe('Toggling of hidden users', () => {
      it('should add a hidden word', () => {
        const stateBefore = initialState;
        const action = toggleMostActiveUser('12345');

        const stateAfter = {
          words: {
            filterTerm: '',
            toToggle: [],
            isToggledActionHide: true,
          },
          users: {
            filterTerm: '',
            toToggle: ['12345'],
