              .then(() =>
                $(`.label.year.league.count.title[data-id="${y}"]`).transition('slide up'))
              .then(() =>
                $(`.label.year.league.count.content[data-id="${y}"]`).transition('slide down'))
              .then(() =>
                $(`.label.year.league.count.content[data-id="${y}"]`).transition('jiggle'));
          } else {
            // otherwise if the tab's title league count was hidden show it and hide the
            // hidden dropdown's count
            if ($(`.label.year.league.count.title[data-id="${y}"]`).hasClass('hidden')) {
              newPromiseChain()
                .then(() =>
                  $(`.label.year.league.count.title[data-id="${y}"]`).transition('slide up'))
                .then(() =>
                  $(`.label.year.league.count.content[data-id="${y}"]`).transition('slide down'));
            }
          }
        }

        let leagueLength = 0;
        if (this.props.football.leagueTeamsByYear[tabPath]) {
          leagueLength = this.props.football.leagueTeamsByYear[tabPath].leagues.length;
        }

        // only fetches the data for the football year if it hasn't already been fetched
        return this.props.onClickYearTab(tabPath, leagueLength);
      },
    });
  }

  render() {
    const currentYear = moment().year() - 1;
    let seasonYearTabsContent = [];
    for (let y = currentYear; y >= EARLIEST_YEAR_AVAILABLE_FROM_FOOTBALL_API; y--) {
      // adding active to the current year to show the initial year's tab
      const tabContentClassName =
        y === currentYear ? 'ui bottom attached active tab segment' : 'ui bottom attached tab segment';

      seasonYearTabsContent.push(
        <div key={`yearcontent${y}`} className={tabContentClassName} data-tab={y}>
          <div className="ui grid">
            <div className="center aligned four wide column">
              <LeagueCategory leagues={this.props.football.seasonsByYear[y]}
                currentYear={currentYear} tabYear={y}
                currentSearchTerms={this.props.currentSearchTerms}
                onClickAddLeague={this.props.onClickAddCategoryFilter}
                onClickRemoveLeague={this.props.onClickRemoveCategoryFilter}
              />
            </div>
            <div className="eight wide column">
              <div className="ui middle aligned grid">
                <div className="five wide column">
                  <TeamCategory teamsByLeague={this.props.football.leagueTeamsByYear[y]}
                    currentSearchTerms={this.props.currentSearchTerms}
                    onClickAddTeam={this.props.onClickAddCategoryFilter}
                    onClickRemoveTeam={this.props.onClickRemoveCategoryFilter}
                    onClickSelectTeam={this.props.onClickSelectTeam}
                  />
                </div>
                <div className="eleven wide column">
                  {
                    !this.props.football.selectedTeam.isSelected &&
                    this.props.football.leagueTeamsByYear[y] &&
                    !this.props.football.leagueTeamsByYear[y].isFetching &&
                      <span className="ui large purple tag label">
                        Select a team to add players...
                      </span>
                  }
                  {
                    this.props.football.selectedTeam.isSelected &&
                      <PlayerCategory teamName={this.props.football.selectedTeam.name}
                        isTeamPlayersFetching={this.props.football.selectedTeam.isFetching}
                        teamCrestUrl={this.props.football.selectedTeam.crestUrl}
                        teamPlayers={this.props.football.selectedTeam.players}
                        currentSearchTerms={this.props.currentSearchTerms}
                        onClickAddPlayer={this.props.onClickAddCategoryFilter}
                        onClickRemovePlayer={this.props.onClickRemoveCategoryFilter}
                      />
                  }
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }

    return (
      <div>
        <YearSelector seasonYears={this.props.football.seasonsByYear} currentYear={currentYear}
          onClickToggleDbOnlySearch={this.props.onClickToggleDbOnlySearch}
        />
        {seasonYearTabsContent}
      </div>
    );
  }
}
CategoryFilters.propTypes = {
  football: React.PropTypes.object,
  onClickCategoryFilter: React.PropTypes.func,
  onClickToggleDbOnlySearch: React.PropTypes.func,
  currentSearchTerms: React.PropTypes.array,
  onClickAddCategoryFilter: React.PropTypes.func,
  onClickRemoveCategoryFilter: React.PropTypes.func,
};

const mapStateToProps = (state) => ({
  football: state.football,
  currentSearchTerms: state.searchTerms,
});

const mapDispatchToProps = (dispatch) => ({
  onClickAddCategoryFilter: (newTerm) => dispatch(addSearchTerm(newTerm)),
  onClickRemoveCategoryFilter: (id) => dispatch(deleteSearchTerm(id)),
  onClickSelectTeam: (id, name, shortName, crestUrl) =>
    dispatch(fetchFootballTeamPlayers(id, name, shortName, crestUrl)),
  onClickYearTab: (year, leagueLength) => {
    dispatch(removeSelectedFootballTeamPlayers());
    if (leagueLength === 0) {
      return dispatch(fetchAllFootballLeagueTeams(year));
    }

    return false;
  },
  onClickToggleDbOnlySearch: () => {
    dispatch(toggleSearchOnlyDb());
  },
});

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(CategoryFilters);

import {
  REQUEST_FOOTBALL_SEASON, RECEIVE_FOOTBALL_SEASON, REMOVE_FOOTBALL_SEASON,
  REQUEST_YEARS_FOOTBALL_LEAGUES_TEAMS, RECEIVE_YEARS_FOOTBALL_LEAGUES_TEAMS,
  SELECT_AND_REQUEST_FOOTBALL_TEAM, RECEIVE_SELECTED_FOOTBALL_TEAM_PLAYERS,
  REMOVE_SELECTED_FOOTBALL_TEAM_PLAYERS
} from './categoryFilterActions';

export const footballCategoryFiltersInitialState = {
  seasonsByYear: {},
  leagueTeamsByYear: {},
  selectedTeam: {
    isSelected: false,
    isFetching: false,
  },
};

const footballCategoryFiltersReducer = (state = footballCategoryFiltersInitialState, action) => {
  switch (action.type) {
  case REQUEST_FOOTBALL_SEASON:
  case RECEIVE_FOOTBALL_SEASON:
    return {
      ...state,
      seasonsByYear: {
        ...state.seasonsByYear,
        [action.year]: footballSeasonReducer(state.seasonsByYear[action.year], action),
      },
    };
  case REMOVE_FOOTBALL_SEASON: {
    const oldSeasonsByYear = state.seasonsByYear;
    delete oldSeasonsByYear[action.year];
    return {
      ...state,
      seasonsByYear: oldSeasonsByYear,
    };
  }
  case REQUEST_YEARS_FOOTBALL_LEAGUES_TEAMS:
  case RECEIVE_YEARS_FOOTBALL_LEAGUES_TEAMS:
    return {
      ...state,
      leagueTeamsByYear: {
        ...state.leagueTeamsByYear,
        [action.year]: footballLeagueTeamsReducer(state.leagueTeamsByYear[action.year], action),
      },
    };
  case SELECT_AND_REQUEST_FOOTBALL_TEAM:
    return {
      ...state,
      selectedTeam: {
        isSelected: true,
        isFetching: true,
        id: action.id,
        name: action.name,
        shortName: action.shortName,
        crestUrl: action.crestUrl,
        players: [],
        count: 0,
      },
    };
  case RECEIVE_SELECTED_FOOTBALL_TEAM_PLAYERS:
    return {
      ...state,
      selectedTeam: {
        ...state.selectedTeam,
        isFetching: false,
        ...action.footballTeamPlayers,
      },
    };
  case REMOVE_SELECTED_FOOTBALL_TEAM_PLAYERS:
    return {
      ...state,
      selectedTeam: {
        isSelected: false,
        isFetching: false,
      },
    };
  default:
    return state;
  }
};

const footballSeasonReducer = (state = { isFetching: false, seasons: [] }, action) => {
  switch (action.type) {
  case REQUEST_FOOTBALL_SEASON:
    return {
      ...state,
      isFetching: true,
    };
  case RECEIVE_FOOTBALL_SEASON:
    return {
      isFetching: false,
      seasons: action.footballSeasons,
    };
  default:
    return state;
  }
};

const footballLeagueTeamsReducer = (state = { isFetching: false, leagues: [] }, action) => {
  switch (action.type) {
  case REQUEST_YEARS_FOOTBALL_LEAGUES_TEAMS:
    return {
      ...state,
      isFetching: true,
    };
  case RECEIVE_YEARS_FOOTBALL_LEAGUES_TEAMS:
    return {
      isFetching: false,
      leagues: action.footballLeagues,
    };
  default:
    return state;
  }
};

export default footballCategoryFiltersReducer;

import { should } from 'chai';
import deepFreeze from 'deep-freeze';
import footballCategoryFiltersReducer, { footballCategoryFiltersInitialState } from './categoryFiltersReducer';
import * as actions from './categoryFilterActions';

describe('#footballCategoryFiltersReducer', () => {
  const year = 2016;
  describe('footballSeason', () => {
    it('should start fetching the requested seasons data', () => {
      const stateBefore = footballCategoryFiltersInitialState;
      const action = actions.requestFootballSeason(year);

      const stateAfter = {
        seasonsByYear: {
          [year]: {
            isFetching: true,
            seasons: [],
          },
        },
        leagueTeamsByYear: {},
        selectedTeam: {
          isSelected: false,
          isFetching: false,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should finish fetching the requested seasons data', () => {
      const exampleJson = { seasons: ['season1', 'season2'] };
      const stateBefore = footballCategoryFiltersReducer(
        footballCategoryFiltersInitialState,
        actions.requestFootballSeason(year)
      );
      const action = actions.receiveFootballSeason(year, exampleJson);

      const stateAfter = {
        seasonsByYear: {
          [year]: {
            isFetching: false,
            seasons: exampleJson,
          },
        },
        leagueTeamsByYear: {},
        selectedTeam: {
          isSelected: false,
          isFetching: false,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });
  });

  describe('years football leagues teams', () => {
    it('should start fetching the requested years football league teams data', () => {
      const stateBefore = footballCategoryFiltersInitialState;
      const action = actions.requestYearsFootballLeaguesTeams(year);

      const stateAfter = {
        seasonsByYear: {},
        leagueTeamsByYear: {
          [year]: {
            isFetching: true,
            leagues: [],
          },
        },
        selectedTeam: {
          isSelected: false,
          isFetching: false,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should finish fetching the requested years football league teams data', () => {
      const exampleJson = { leagues: ['league1', 'league2'] };
      const stateBefore = footballCategoryFiltersReducer(
        footballCategoryFiltersInitialState,
        actions.requestYearsFootballLeaguesTeams(year)
      );
      const action = actions.receiveYearsFootballLeaguesTeams(year, exampleJson);

      const stateAfter = {
        seasonsByYear: {},
        leagueTeamsByYear: {
          [year]: {
            isFetching: false,
            leagues: exampleJson,
          },
        },
        selectedTeam: {
          isSelected: false,
          isFetching: false,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });
  });

  describe('football team players', () => {
    const id = 1;
    const name = 'example football team';
    const shortName = 'ExFootTeam';
    const crestUrl = 'http://exFootTeamCrest.com';

    it('should select and start fetching the requested football team players', () => {
      const stateBefore = footballCategoryFiltersInitialState;
      const action = actions.selectAndRequestFootballTeam(id, name, shortName, crestUrl);

      const stateAfter = {
        seasonsByYear: {},
        leagueTeamsByYear: {},
        selectedTeam: {
          isSelected: true,
          isFetching: true,
          id,
          name,
          shortName,
          crestUrl,
          players: [],
          count: 0,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should finish fetching the requested football team players', () => {
      const exampleJson = { players: ['player1', 'player2'], count: 10 };
      const stateBefore = footballCategoryFiltersReducer(
        footballCategoryFiltersInitialState,
        actions.selectAndRequestFootballTeam(id, name, shortName, crestUrl)
      );
      const action = actions.receiveSelectedFootballTeamPlayers(exampleJson);

      const stateAfter = {
        seasonsByYear: {},
        leagueTeamsByYear: {},
        selectedTeam: {
          isSelected: true,
          isFetching: false,
          id,
          name,
          shortName,
          crestUrl,
          ...exampleJson,
        },
      };

      deepFreeze(stateBefore);
      deepFreeze(action);

      footballCategoryFiltersReducer(stateBefore, action).should.deep.equal(stateAfter);
    });

    it('should remove the selected football team players', () => {
      const exampleJson = { players: ['player1', 'player2'] };
