import moment from 'moment';
import {
  ADD_SEARCH_TERM, TOGGLE_SEARCH_TERM_PARAMTYPE_SELECTION, DELETE_SEARCH_TERM,
  RECEIVE_FEED_RESULTS, SET_FEED_PAGE_NUMBER, SET_FEED_PAGE_LIMIT, TOGGLE_SEARCH_ONLY_DB,
  INVALIDATE_FEED_RESULTS
} from './searchActions';
import { createTwitterParamTypes, toggleParamType } from '../../shared/utilities';
import { groupedCountWords, mostFrequentWords, mostFrequentUsers } from './../tweetAnalysis';

export const searchTermsReducer = (state = [], action) => {
  switch (action.type) {
  case ADD_SEARCH_TERM:
    return [
      ...state,
      searchTermReducer(undefined, action),
    ];
  case TOGGLE_SEARCH_TERM_PARAMTYPE_SELECTION:
    return state.map(searchTerm => searchTermReducer(searchTerm, action));
  case DELETE_SEARCH_TERM: {
    if (state.length === 1) return [];

    const termIndex = state.map(term => (term.id)).indexOf(action.id);
    return [
      ...state.slice(0, termIndex),
      ...state.slice(termIndex + 1),
    ];
  }
  default:
    return state;
  }
};

const searchTermReducer = (state, action) => {
  switch (action.type) {
  case ADD_SEARCH_TERM:
    return {
      id: action.id,
      query: action.query,
      paramTypes: createTwitterParamTypes(action.paramTypes),
      source: action.source,
    };
  case TOGGLE_SEARCH_TERM_PARAMTYPE_SELECTION: {
    if (state.id !== action.id) {
      return state;
    }

    return {
      ...state,
      paramTypes: toggleParamType(state.paramTypes, action.paramTypeName),
    };
  }

  default:
    return state;
  }
};

export const searchOnlyDB = (state = false, action) => {
  switch (action.type) {
  case TOGGLE_SEARCH_ONLY_DB:
    return !state;
  default:
    return state;
  }
};

export const feedReducer = (state = { posts: [], paginationInfo: { number: 1, limit: 10 }, groupedMostFrequentWords: [], mostFrequentUsers: [], lastRequestId: 0 }, action) => {
  switch (action.type) {
    case INVALIDATE_FEED_RESULTS:
      return {
        ...state,
        lastRequestId: action.requestId,
      };
    case RECEIVE_FEED_RESULTS:
      if (state.lastRequestId > action.requestId) {
        // Response is from an old request, ignore it
        return state;
      } else {
        // Response is from the most recent request, actually show the posts
        return {
          ...state,
          posts: sortPostsForFeed(action.data.data.records),
          groupedMostFrequentWords: groupedCountWords(mostFrequentWords(action.data.data.records.map((post) => post.data.content))),
          mostFrequentUsers: mostFrequentUsers(action.data.data.records),
        };
      }
    case SET_FEED_PAGE_NUMBER:
      return { ...state, paginationInfo: { ...state.paginationInfo, number: action.number } };
    case SET_FEED_PAGE_LIMIT:
      return { ...state, paginationInfo: { ...state.paginationInfo, limit: action.limit } };
    default:
      return state;
  }
};

const sortPostsForFeed = (feed) => (
  [...feed].sort(
    (post1, post2) => (
      moment(post2.data.date).diff(moment(post1.data.date))
    )
  )
);

import { should } from 'chai';
import deepFreeze from 'deep-freeze';
import { searchTermsReducer, feedReducer } from './searchReducer';
import * as actions from './searchActions';
import { createTwitterParamTypes } from '../../shared/utilities';
import { groupedCountWords, mostFrequentWords, mostFrequentUsers } from './../tweetAnalysis';

describe('#SearchTermsReducer', () => {
  it('should add a hashtag search term', () => {
    const stateBefore = [];
    const action = actions.addSearchTerm('#Football');

    const stateAfter = [{
      id: action.id,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['hashtag']),
      source: 'twitter',
    }, ];

    deepFreeze(stateBefore);
    deepFreeze(action);

    searchTermsReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('should add a new query to existing queries', () => {
    const stateBefore = [{
      id: 0,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['mention']),
      source: 'twitter',
    }, ];
    const action = actions.addSearchTerm('@Manchester');

    const stateAfter = [
      ...stateBefore,
      {
        id: 9,
        query: 'Manchester',
        paramTypes: createTwitterParamTypes(['mention', 'author']),
        source: 'twitter',
      }, ];

    deepFreeze(stateBefore);
    deepFreeze(action);

    searchTermsReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('should return empty search terms when deleting search terms with single term', () => {
    const stateBefore = [{
      id: 0,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['mention']),
      source: 'twitter',
    }, ];
    const action = actions.deleteSearchTerm(0);

    const stateAfter = [];

    deepFreeze(stateBefore);
    deepFreeze(action);

    searchTermsReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('should delete term with id', () => {
    const stateBefore = [{
      id: 0,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['mention']),
      source: 'twitter',
    }, {
      id: 1,
      query: 'Manchester',
      paramTypes: createTwitterParamTypes(['hashtag', 'author']),
      source: 'twitter',
    }, ];
    const action = actions.deleteSearchTerm(0);

    const stateAfter = [{
      id: 1,
      query: 'Manchester',
      paramTypes: createTwitterParamTypes(['hashtag', 'author']),
      source: 'twitter',
    }, ];

    deepFreeze(stateBefore);
    deepFreeze(action);

    searchTermsReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('should edit term paramType with id', () => {
    const stateBefore = [{
      id: 0,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['mention']),
      source: 'twitter',
    }, {
      id: 1,
      query: 'Manchester',
      paramTypes: createTwitterParamTypes(['hashtag', 'author']),
      source: 'twitter',
    }, ];
    const action = actions.toggleSearchTermParamTypeSelection(0, 'author');

    const stateAfter = [{
      id: 0,
      query: 'Football',
      paramTypes: createTwitterParamTypes(['author', 'mention']),
      source: 'twitter',
    }, {
      id: 1,
      query: 'Manchester',
      paramTypes: createTwitterParamTypes(['hashtag', 'author']),
      source: 'twitter',
    }, ];

    deepFreeze(stateBefore);
    deepFreeze(action);

    searchTermsReducer(stateBefore, action).should.deep.equal(stateAfter);
  });
});

describe('#FeedReducer', () => {
  it('can receive new feed results, and will group and sort them', () => {
    const stateBefore = {};

    const records = [
      { data:{ content:'some record', date: '2016-04-06' }, author:{ id: '1' } },
      { data:{ content:'another', date: '2014-04-06' }, author:{ id: '2' } },
    ];

    const action = {
      type: actions.RECEIVE_FEED_RESULTS,
      data: {
        data: {
          records: records,
        },
      },
    };

    const stateAfter = {
      posts: records,
      groupedMostFrequentWords: groupedCountWords(mostFrequentWords(records.map((post) => post.data.content))),
      mostFrequentUsers: mostFrequentUsers(records),
    };

    deepFreeze(stateBefore);
    deepFreeze(action);

    feedReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('can set the pagination page', () => {
    const stateBefore = { paginationInfo: { number: 1, limit: 10 } };

    const action = {
      type: actions.SET_FEED_PAGE_NUMBER,
      number: 5,
    };

    const stateAfter = { paginationInfo: { number: 5, limit: 10 } };

    deepFreeze(stateBefore);
    deepFreeze(action);

    feedReducer(stateBefore, action).should.deep.equal(stateAfter);
  });

  it('can set the pagination limit', () => {
    const stateBefore = { paginationInfo: { number: 1, limit: 10 } };

    const action = {
      type: actions.SET_FEED_PAGE_LIMIT,
      limit: 5,
    };

    const stateAfter = { paginationInfo: { number: 1, limit: 5 } };

    deepFreeze(stateBefore);
    deepFreeze(action);

    feedReducer(stateBefore, action).should.deep.equal(stateAfter);
  });
});

import React, { Component } from 'react';
import { connect } from 'react-redux';
import { throttleFunction } from './../../shared/utilities';
import TermItem from './TermItem';
import {
  deleteSearchTerm, invalidateFeedResults, toggleSearchTermParamTypeSelection
} from './searchActions';

class TermsList extends Component {
  componentWillReceiveProps() {
    this.props.onSearchTermsChange();
    $('.icon.param.types.popup').popup();
  }

  render() {
    return (
      <div onClick={this.props.showSearchTerm}>
        <i className="icon search"></i>
        {this.props.searchTerms.map(term => (
          <TermItem
            onToggleParamTypeClick={(paramTypeToggle) =>
              this.props.onSearchTermParamTypeToggleClick(term.id, paramTypeToggle)
            }
            key={term.id}
            {...term}
            onDeleteClick={() => this.props.onSearchTermDeleteClick(term.id)}
          />
        ))}
      </div>
    );
  }
}
TermsList.propTypes = {
  searchTerms: React.PropTypes.array,
  showSearchTerm: React.PropTypes.func,
  onSearchTermParamTypeToggleClick: React.PropTypes.func,
  onSearchTermDeleteClick: React.PropTypes.func,
  onSearchTermsChange: React.PropTypes.func,
};

const mapStateToProps = (state) => ({ searchTerms: state.searchTerms });

const mapDispatchToProps = (dispatch) => ({
  onSearchTermParamTypeToggleClick: (id, paramTypeName) => {
    dispatch(toggleSearchTermParamTypeSelection(id, paramTypeName));
  },
  onSearchTermDeleteClick: (id) => {
    dispatch(deleteSearchTerm(id));
  },
  onSearchTermsChange: throttleFunction(() => dispatch(invalidateFeedResults()), 1000),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TermsList);

import React, { Component } from 'react';

const TermItem = ({ onDeleteClick, onToggleParamTypeClick, query, source, paramTypes, id }) => (
  <div data-id={id} className="ui multiple dropdown labeled icon violet button"
    onClick={(e) => e.stopPropagation()}
  >
    <div className="ui grid">
      <div className="two wide middle aligned column" style={{ paddingLeft: 0 }}>
        <i className={`large blue ${source} icon`}></i>
      </div>
      <div className="thirteen wide center aligned column">
        <div style={{ fontSize: '1.25em', whiteSpace: 'nowrap', paddingBottom: '5px' }}>
          {query}
        </div>
        <div>
          {paramTypes
            .filter((paramType) => paramType.selected)
            .map((paramType, paramId) => {
              // This is to create an icon with className or not depending on if
              // the param type icon is a semantic class (ie #)
              if (paramType.icon.length > 1) {
                return (
                  <i key={paramId}
                    className={`tiny inverted blue circular ${paramType.icon} param types popup`}
                    data-title={`Search by ${paramType.name}`}
                  />
                );
              } else {
                return (
                  <i key={paramId} className={'tiny inverted blue circular icon param types popup'}
                    data-title={`Search by ${paramType.name}`}
                  >
                    {paramType.icon}
                  </i>
                );
              }
            })
          }
        </div>
        <div>
          <i className="dropdown icon" style={{ paddingTop: 0, paddingRight: '25px' }}></i>
        </div>
      </div>
    </div>
    <i className="delete icon" onClick={() => onDeleteClick()}></i>
    <TermItemMenu
      termId={id}
      highlightColor={source === 'twitter' ? 'blue' : ''}
      paramTypes={paramTypes}
      onToggleParamType={(paramTypeToggle) => onToggleParamTypeClick(paramTypeToggle)}
    />
  </div>
);

class TermItemMenu extends Component {
  componentDidMount() {
    $(`.ui.dropdown[data-id="${this.props.termId}"]`)
      .dropdown({
        transition: 'drop',
        action: (value, text) => {
          this.props.onToggleParamType(text);
        },
      });
  }

  render() {
    const menuItems = this.props.paramTypes.map((paramType, id) => {
      let paramTypeIcon;
      const highlighted = paramType.selected ? this.props.highlightColor : 'black';

      // This is to create an icon with className or not depending on if
      // the param type icon is a semantic class (ie #)
      if (paramType.icon.length > 1) {
        paramTypeIcon = <i className={`inverted ${highlighted} circular ${paramType.icon}`}></i>;
      } else {
        paramTypeIcon = <i className={`inverted ${highlighted} circular icon`}>{paramType.icon}</i>;
      }

      return (
        <div key={id} data-value={paramType.name} className="item">
          {paramTypeIcon}
          <span className={`ui ${highlighted} label`}>{paramType.name}</span>
        </div>
      );
    });

    return (
      <div className="menu">
        {menuItems}
      </div>
    );
  }
}

export default TermItem;

