            .then(leagueTeams => ({
              name: league.name,
              id: league.id,
              ...leagueTeams,
            }))
        )
      )
    )
    .then(
      (allYearsLeagueTeams) => {
        res.writeHead(200, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({
          data: {
            teamsByLeague: allYearsLeagueTeams,
          },
        }));
