      type: 'ADD_SEARCH_TERM',
      id: searchTermId++,
      query: expectedQuery,
      paramTypes: ['author', 'hashtag', 'mention'],
      source: 'twitter',
    };

    actions.addSearchTerm(query).should.deep.equal(expectedAction);
