import { should } from 'chai';
import { mostFrequentWords, mostFrequentUsers, groupedCountWords } from './tweetAnalysis';

should();

describe('#TweetAnalysis', () => {
  describe('Frequent words counter', () => {
    const exampleFrequentWords = [
      { word: 'three', count: 3 },
      { word: 'two', count: 2 },
      { word: 'one', count: 1 },
    ];

    it('returns an empty list on empty input', () => {
      const tweets = [];
      mostFrequentWords(tweets).should.deep.equal([]);
    });

    it('counts words in a single tweet, most frequent first', () => {
      const tweet = ['one three two three two three'];
      mostFrequentWords(tweet).should.deep.equal(exampleFrequentWords);
    });

    it('counts words across several tweets, most frequent first', () => {
      const tweets = ['one two three three', 'two three'];
      mostFrequentWords(tweets).should.deep.equal(exampleFrequentWords);
    });

    it('ignores punctuation when splitting words', () => {
      const tweets = ['one, two,two three.three !three'];
      mostFrequentWords(tweets).should.deep.equal(exampleFrequentWords);
    });

    it('correctly identifies and removes t.co URLS', () => {
      const tweets = ['one, two three https://t.co/url1 https://t.co/url2 four https://t.co/url3'];
      mostFrequentWords(tweets).should.deep.equal([
        { word: 'one', count: 1 },
        { word: 'two', count: 1 },
        { word: 'three', count: 1 },
        { word: 'four', count: 1 },
      ]);
    });

    it('correctly identifies @mentions', () => {
      const tweets = ['one, @Winwardo two'];
      mostFrequentWords(tweets).should.deep.equal([
        { word: 'one', count: 1 },
        { word: '@Winwardo', count: 1 },
        { word: 'two', count: 1 },
      ]);
    });

    it('correctly identifies #hashtags', () => {
      const tweets = ['one, #FOOTBALL two'];
      mostFrequentWords(tweets).should.deep.equal([
        { word: 'one', count: 1 },
        { word: '#FOOTBALL', count: 1 },
        { word: 'two', count: 1 },
      ]);
    });

    it('can conflate words of different cases together with one word', () => {
      const exampleCountedAndSortedWords = [
        { word: 'football', count: 10 },
      ];

      groupedCountWords(exampleCountedAndSortedWords).should.deep.equal(
        [
          {
            word: 'football',
            count: 10,
            makeup: [
              { word: 'football', count: 10 },
            ],
          },
        ]
      );
    });

    it('can conflate words of different cases together with one uppercase word', () => {
      const exampleCountedAndSortedWords = [
        { word: 'FOOTBALL', count: 10 },
      ];

      groupedCountWords(exampleCountedAndSortedWords).should.deep.equal(
        [
          {
            word: 'football',
            count: 10,
            makeup: [
              { word: 'FOOTBALL', count: 10 },
            ],
          },
        ]
      );
    });

    it('can conflate words of different cases together with one uppercase word and one lower case', () => {
      const exampleCountedAndSortedWords = [
        { word: 'FOOTBALL', count: 10 },
        { word: 'football', count: 5 },
      ];

      groupedCountWords(exampleCountedAndSortedWords).should.deep.equal(
        [
          {
            word: 'football',
            count: 15,
            makeup: [
              { word: 'FOOTBALL', count: 10 },
              { word: 'football', count: 5 },
            ],
          },
        ]
      );
    });

    it('can conflate hashtags with normal words', () => {
      const exampleCountedAndSortedWords = [
        { word: 'Football', count: 10 },
        { word: '#footBALL', count: 5 },
      ];

      groupedCountWords(exampleCountedAndSortedWords).should.deep.equal(
        [
          {
            word: 'football',
            count: 15,
            makeup: [
              { word: 'Football', count: 10 },
              { word: '#footBALL', count: 5 },
            ],
          },
        ]
      );
    });

    it('can conflate words of different cases together in a complex example', () => {
      const exampleCountedAndSortedWords = [
        { word: 'MANCHESTER', count: 16 },
        { word: 'football', count: 10 },
        { word: 'liverpool', count: 7 },
        { word: 'Football', count: 5 },
        { word: 'FOOTBALL', count: 5 },
      ];

      groupedCountWords(exampleCountedAndSortedWords).should.deep.equal(
        [
          {
            word: 'football',
            count: 20,
            makeup: [
              { word: 'football', count: 10 },
              { word: 'Football', count: 5 },
              { word: 'FOOTBALL', count: 5 },
            ],
          },
          {
            word: 'manchester',
            count: 16,
            makeup: [
              { word: 'MANCHESTER', count: 16 },
            ],
          },
          {
            word: 'liverpool',
            count: 7,
            makeup: [
              { word: 'liverpool', count: 7 },
            ],
          },
        ]
      );
    });
  });

  describe('Most active users counter', () => {
    it('returns an empty list on an empty input', () => {
      const tweets = [];
      mostFrequentUsers(tweets).should.deep.equal([]);
    });

    it('returns the Tweeter and their tweets', () => {
      const tweets = [
        {
          author: {
            id: 1,
            name: 'example',
            handle: 'exampleHandle',
          },
          data: { content: 'hello world' },
          source: 'twitter',
        }, {
          author: {
            id: 1,
            name: 'example',
            handle: 'exampleHandle',
          },
          data: { content: 'second tweet' },
          source: 'twitter',
        },
      ];
      mostFrequentUsers(tweets).should.deep.equal(
        [
          {
            author: {
              id: 1,
              name: 'example',
              handle: 'exampleHandle',
            },
            posts: [
              { content: 'hello world' },
              { content: 'second tweet' },
            ],
            source: 'twitter',
          },
        ]
      );
    });

    it('returns the Tweeter and their tweets, sorted by most active first', () => {
      const tweets = [
        {
          author: {
            id: 2,
            name: 'example2',
            handle: 'exampleHandle2',
          },
          data: { content: 'hello world' },
          source: 'twitter',
        }, {
          author: {
            id: 1,
            name: 'example',
            handle: 'exampleHandle',
          },
          data: { content: 'hey there' },
          source: 'twitter',
        }, {
          author: {
            id: 1,
            name: 'example',
            handle: 'exampleHandle',
          },
          data: { content: 'I am second' },
          source: 'twitter',
        }, {
          author: {
            id: 2,
            name: 'example2',
            handle: 'exampleHandle2',
          },
          data: { content: 'second tweet' },
          source: 'twitter',
        }, {
          author: {
            id: 3,
            name: 'example3',
            handle: 'exampleHandle3',
          },
          data: { content: 'I am third' },
          source: 'twitter',
        }, {
          author: {
            id: 2,
            name: 'example2',
            handle: 'exampleHandle2',
          },
          data: { content: 'I am first' },
          source: 'twitter',
        },
      ];
      mostFrequentUsers(tweets).should.deep.equal(
        [
          {
            author: {
              id: 2,
              name: 'example2',
              handle: 'exampleHandle2',
            },
            posts: [
              { content: 'hello world' },
              { content: 'second tweet' },
              { content: 'I am first' },
            ],
            source: 'twitter',
          },
          {
            author: {
              id: 1,
              name: 'example',
              handle: 'exampleHandle',
            },
            posts: [
              { content: 'hey there' },
              { content: 'I am second' },
            ],
            source: 'twitter',
          },
          {
            author: {
              id: 3,
              name: 'example3',
              handle: 'exampleHandle3',
            },
            posts: [
              { content: 'I am third' },
            ],
            source: 'twitter',
          },
        ]
      );
    });
  });
});

import { should } from 'chai';
import * as tweetFinder from './tweetFinder';

describe('#TweetFinder', () => {
  describe('Unioning tweets', () => {
    it('should return an empty list given an empty list', () => {
      const inputResultList = [];
      const expected = [];

      tweetFinder.unionTweets(inputResultList).should.deep.equals(expected);
    });

    it('should return the identity of a single resultlist', () => {
      const inputResultList = [[makeTweetWithId(10)]];
      const expected = [makeTweetWithId(10)];

      tweetFinder.unionTweets(inputResultList).should.deep.equals(expected);
    });

    it('should append two unique lists together', () => {
      const inputResultList = [[makeTweetWithId(10)], [makeTweetWithId(33)]];
      const expected = [makeTweetWithId(10), makeTweetWithId(33)];

      tweetFinder.unionTweets(inputResultList).should.deep.equals(expected);
    });

    it('should union two non-unique lists together', () => {
      const inputResultList = [[makeTweetWithId(10), makeTweetWithId(50)], [makeTweetWithId(33), makeTweetWithId(50)]];
      const expected = [makeTweetWithId(10), makeTweetWithId(33), makeTweetWithId(50)];

      tweetFinder.unionTweets(inputResultList).should.deep.equals(expected);
    });

    const makeTweetWithId = (id, content='Hello world') => ({ tweet: { id: id, content: content } });

  });
});

describe('#QueryBuilder', () => {
  const makeQuery = (term, paramTypes) => {
    const result = {
      query: term,
      paramTypes: [],
    };
    for (const paramTypeName of paramTypes) {
      result.paramTypes.push({
        name: paramTypeName,
        selected: true,
      });
    }
    return result;
  };

  it('should return the empty array for an empty array', () => {
    tweetFinder.buildTwitterQuery([]).should.deep.equal([]);
  });

  it('should build a single Twitter query for a single search query', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['keyword', 'author', 'hashtag', 'mention']),
      ]
    ).should.deep.equal(
      [
        '"arsenal" OR @arsenal OR #arsenal',
      ]
    );
  });

  it('should build a single Twitter query for a single search query with differently ordered paraas', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['hashtag', 'keyword', 'author', 'mention']),
      ]
    ).should.deep.equal(
      [
        '#arsenal OR "arsenal" OR @arsenal',
      ]
    );
  });

  it('should remove spaces in authors/mentions and hashtags', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('manchester united fc', ['keyword', 'author', 'hashtag', 'mention']),
      ]
    ).should.deep.equal(
      [
        '"manchester united fc" OR @manchesterunitedfc OR #manchesterunitedfc',
      ]
    );
  });

  it('should build a single Twitter query for a multiple search query', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['keyword', 'author', 'hashtag', 'mention']),
        makeQuery('spurs', ['keyword', 'author', 'hashtag', 'mention']),
      ]
    ).should.deep.equal(
      [
        '"arsenal" OR @arsenal OR #arsenal OR "spurs" OR @spurs OR #spurs',
      ]
    );
  });

  it('should build two Twitter queries for a large multiple search query, splitting on query term', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['keyword', 'author', 'hashtag', 'mention']),
        makeQuery('spurs', ['keyword', 'author', 'hashtag', 'mention']),
        makeQuery('manchester', ['keyword', 'author', 'hashtag', 'mention']),
        makeQuery('liverpool', ['keyword', 'author', 'hashtag', 'mention']),
      ]
    ).should.deep.equal(
      [
        '"arsenal" OR @arsenal OR #arsenal OR "spurs" OR @spurs OR #spurs OR "manchester" OR @manchester OR #manchester',
        '"liverpool" OR @liverpool OR #liverpool',
      ]
    );
  });

  it('should be able to search just keywords', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['keyword']),
      ]
    ).should.deep.equal(
      [
        '"arsenal"',
      ]
    );
  });

  it('should be able to search just hashtags', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['hashtag']),
      ]
    ).should.deep.equal(
      [
        '#arsenal',
      ]
    );
  });

  it('should conflate mention and author', () => {
    tweetFinder.buildTwitterQuery(
      [
        makeQuery('arsenal', ['mention']),
        makeQuery('spurs', ['author']),
      ]
    ).should.deep.equal(
      [
        '@arsenal OR @spurs',
      ]
    );
  });
});

describe('#QueryNormaliser', () => {
  it('leaves short single terms alone', () => {
    tweetFinder.normaliseQueryTerm('hi').should.equal('hi');
  });

  it('fuzzes longer single terms', () => {
    tweetFinder.normaliseQueryTerm('manchester').should.equal('manchester~');
  });

  it('surrounds multiple term queries in quotes', () => {
    tweetFinder.normaliseQueryTerm('manchester united').should.equal('"manchester united"');
  });
});

